list-view-config.query-select:
PREFIX rdfs: <http://www.w3.org/2000/01/rdf-schema#> PREFIX core: <http://vivoweb.org/ontology/core#> PREFIX foaf: <http://xmlns.com/foaf/0.1/> PREFIX vitro: <http://vitro.mannlib.cornell.edu/ns/vitro/0.7#> PREFIX obo: <http://purl.obolibrary.org/obo/> PREFIX vivofr: <http://data.ehess.fr/ontology/vivo-fr#>
SELECT DISTINCT ?subclass ?membership ?membershipTitle ?membershipLabel ?hrJobTitle ?rank ?org ?orgName ?middleOrg ?middleOrgName ?outerOrg ?outerOrgName ?dateTimeStart ?dateTimeEnd WHERE { ?subject ?property ?membership . ?membership a vivofr:MMB_0000001 . OPTIONAL { ?subject ?property ?membership . ?membership a vivofr:MMB_0000001 . ?membership rdfs:label ?membershipLabel . } OPTIONAL { ?subject ?property ?membership . ?membership a vivofr:MMB_0000001 . ?membership core:relates ?org . ?org a foaf:Organization . ?org rdfs:label ?orgName . } OPTIONAL { ?subject ?property ?membership . ?membership a vivofr:MMB_0000001 . ?membership core:relates ?org . ?org a foaf:Organization . ?org rdfs:label ?orgName . ?middleOrg obo:BFO_0000050 ?org . ?middleOrg rdfs:label ?middleOrgName . } OPTIONAL { ?subject ?property ?membership . ?membership a vivofr:MMB_0000001 . ?membership core:relates ?org . ?org a foaf:Organization . ?org rdfs:label ?orgName . ?org obo:BFO_0000050 ?middleOrg . ?middleOrg obo:BFO_0000050 ?outerOrg . ?outerOrg rdfs:label ?outerOrgName . } OPTIONAL { ?subject ?property ?membership . ?membership a vivofr:MMB_0000001 . ?membership vitro:mostSpecificType ?subclass . ?subclass rdfs:subClassOf vivofr:MMB_0000001 . ?subclass rdfs:label ?membershipTitle . } OPTIONAL { ?subject ?property ?membership . ?membership a vivofr:MMB_0000001 . ?membership core:hrJobTitle ?hrJobTitle . } OPTIONAL { ?subject ?property ?membership . ?membership a vivofr:MMB_0000001 . ?membership core:rank ?rank . } OPTIONAL { ?subject ?property ?membership . ?membership a vivofr:MMB_0000001 . ?membership core:dateTimeInterval ?dateTimeInterval . ?dateTimeInterval core:start ?dateTimeStartValue . ?dateTimeStartValue core:dateTime ?dateTimeStart . } OPTIONAL { ?subject ?property ?membership . ?membership a vivofr:MMB_0000001 . ?membership core:dateTimeInterval ?dateTimeInterval . ?dateTimeInterval core:end ?dateTimeEndValue . ?dateTimeEndValue core:dateTime ?dateTimeEnd . }
OPTIONAL { ?subject ?property ?membership . ?membership a vivofr:MMB_0000001 . ?membership vitro:mostSpecificType ?subclass . ?subclass rdfs:subClassOf vivofr:MMB_0000001 . }
FILTER ( bound(?org) )
} ORDER BY ?subclass DESC(?dateTimeEnd) ?rank
